# --- Worker Class for the Analysis ---
class AnalysisWorker(QObject):
    """ Worker that performs sequence analysis in a separate thread. """
    # --- Signals ---
    # Progress signal (integer percentage from 0 to 100)
    progress = pyqtSignal(int)
    # Signal to update the status text in the GUI
    status = pyqtSignal(str)
    # Signal emitted when the job is finished, carries the results
    finished_signal = pyqtSignal(dict, list, list, str) # stats, successful_frames, corrupted_files, image_type
    # Signal emitted when the job is cancelled by the user
    cancelled_signal = pyqtSignal()
    # Signal to handle any errors
    error_signal = pyqtSignal(str)

    def __init__(self, siril, files_to_process, frame_indices, total_seq_number):
        super().__init__()
        self.siril = siril
        self.initial_files_to_process = files_to_process
        self.initial_frame_indices = frame_indices
        self.total_sequence_number = total_seq_number
        self.is_cancelled = False

    def run(self):
        """ Main method that performs sequence analysis. """
        try:
            # --- Read and Analyze Files ---
            # Lists for data of successfully read files only
            successful_frames_indices = []
            stats = {}
            image_type = None # Will be 'mono', 'cfa', or 'rgb'
            corrupted_files = [] # List to store problematic files
            total_initial_files = len(self.initial_files_to_process)

            for i, frame_path in enumerate(self.initial_files_to_process):
                # Check if the operation was cancelled by the user
                if self.is_cancelled:
                    self.status.emit("Analysis cancelled by user.")
                    self.cancelled_signal.emit()
                    break # Exit the loop

                # Try...except block to handle errors on individual files
                try:
                    # Open the FITS file and get the data and header
                    with fits.open(frame_path) as hdul:
                        # Verify that the header and data are accessible
                        if not hdul or not hasattr(hdul[0], 'data') or not hasattr(hdul[0], 'header'):
                            raise IOError("Invalid FITS structure: HDU[0] is missing data or header.")
                        data = hdul[0].data
                        if data is None:
                            raise IOError("Invalid FITS structure: HDU[0] data is None.")
                        data = data.astype(np.float32)
                        header = hdul[0].header
                    
                    current_frame_type = self._get_image_type(data, header)

                    # On the first frame, determine the image type and initialize stats dict
                    if not stats: # Initialize only if the dictionary is empty
                        image_type = current_frame_type
                        # Initialize the stats dictionary based on the detected type
                        if image_type in ['rgb', 'cfa']:
                            stats = {
                                'min_r': [], 'max_r': [], 'mean_r': [], 'median_r': [], 'std_r': [],
                                'min_g': [], 'max_g': [], 'mean_g': [], 'median_g': [], 'std_g': [],
                                'min_b': [], 'max_b': [], 'mean_b': [], 'median_b': [], 'std_b': []
                            }
                        else:
                            stats = {'min': [], 'max': [], 'mean': [], 'median': [], 'std': []}
                    
                    # self.siril.log(f"{header.get('BAYERPAT')} {header.get('ROWORDER')} {image_type}", s.LogColor.GREEN)

                    # Process based on detected type
                    if image_type == 'mono':
                        stats['min'].append(np.min(data))
                        stats['max'].append(np.max(data))
                        stats['mean'].append(np.mean(data))
                        stats['median'].append(np.median(data))
                        stats['std'].append(np.std(data))
                    
                    elif image_type == 'cfa':
                        pattern = header.get('BAYERPAT', 'RGGB').upper()
                        roworder = header.get('ROWORDER', 'BOTTOM-UP').upper()
                        if roworder == 'BOTTOM-UP':
                            data = np.flipud(data)

                        if pattern == 'RGGB':
                            r_pixels = data[0::2, 0::2].ravel()
                            g1_pixels = data[0::2, 1::2].ravel()
                            g2_pixels = data[1::2, 0::2].ravel()
                            b_pixels = data[1::2, 1::2].ravel()
                        elif pattern == 'GRBG':
                            g1_pixels = data[0::2, 0::2].ravel()
                            r_pixels = data[0::2, 1::2].ravel()
                            b_pixels = data[1::2, 0::2].ravel()
                            g2_pixels = data[1::2, 1::2].ravel()
                        elif pattern == 'GBRG':
                            g1_pixels = data[0::2, 0::2].ravel()
                            b_pixels = data[0::2, 1::2].ravel()
                            r_pixels = data[1::2, 0::2].ravel()
                            g2_pixels = data[1::2, 1::2].ravel()
                        elif pattern == 'BGGR':
                            b_pixels = data[0::2, 0::2].ravel()
                            g1_pixels = data[0::2, 1::2].ravel()
                            g2_pixels = data[1::2, 0::2].ravel()
                            r_pixels = data[1::2, 1::2].ravel()
                        else:
                            if self.siril: self.siril.log(f"Unsupported BAYERPAT '{pattern}'. Defaulting to RGGB.", s.LogColor.RED)
                            # Default to RGGB if pattern is unknown
                            r_pixels = data[0::2, 0::2].ravel()
                            g1_pixels = data[0::2, 1::2].ravel()
                            g2_pixels = data[1::2, 0::2].ravel()
                            b_pixels = data[1::2, 1::2].ravel()

                        # Combine the two green channels for statistics as requested
                        g_pixels = np.concatenate((g1_pixels, g2_pixels))
                        
                        for channel_pixels, chan_char in zip([r_pixels, g_pixels, b_pixels], ['r', 'g', 'b']):
                            stats[f'min_{chan_char}'].append(np.min(channel_pixels))
                            stats[f'max_{chan_char}'].append(np.max(channel_pixels))
                            stats[f'mean_{chan_char}'].append(np.mean(channel_pixels))
                            stats[f'median_{chan_char}'].append(np.median(channel_pixels))
                            stats[f'std_{chan_char}'].append(np.std(channel_pixels))
                    
                    elif image_type == 'rgb':
                        for chan_idx, chan_char in enumerate(['r', 'g', 'b']):
                            channel_data = data[chan_idx, :, :]
                            stats[f'min_{chan_char}'].append(np.min(channel_data))
                            stats[f'max_{chan_char}'].append(np.max(channel_data))
                            stats[f'mean_{chan_char}'].append(np.mean(channel_data))
                            stats[f'median_{chan_char}'].append(np.median(channel_data))
                            stats[f'std_{chan_char}'].append(np.std(channel_data))
                    
                    # If the file has been processed, add its frame number to the hit list.
                    successful_frames_indices.append(self.initial_frame_indices[i])
                
                # Except block to handle single file read errors
                except (IOError, OSError, ValueError) as e:
                    filename = os.path.basename(frame_path)
                    # self.siril.log(f"WARNING: Could not process file '{filename}'. Reason: {e}", "yellow")
                    self.siril.log(f"WARNING: Could not process file '{filename}'.\nReason: {e}", s.LogColor.RED)
                    corrupted_files.append(filename)
                    continue # Skip to the next file in the loop

                # Outputs status and progress signals
                progress_percent = int(((i + 1) / total_initial_files) * 100)
                self.progress.emit(progress_percent)
                status_text = f"Processing frame {i+1} / {total_initial_files}\nTotal in sequence: {self.total_sequence_number} - ({image_type.upper() if image_type else '...'})"
                self.status.emit(status_text)
            
            # If the loop is finished (not cleared), emit the end signal
            if not self.is_cancelled:
                self.finished_signal.emit(stats, successful_frames_indices, corrupted_files, image_type)

        except Exception as e:
            self.error_signal.emit(f"Error in processing thread: {str(e)}")
            traceback.print_exc()

    def stop(self):
        """ Method to set the delete flag. """
        self.is_cancelled = True

    def _get_image_type(self, data, header):
        """ Determines image type (mono, cfa, rgb) based on FITS header keywords, """
        # Check for either BAYERPAT or CFAIMAG for more robust CFA detection.
        if data.ndim == 2 and (header.get('BAYERPAT') or header.get('CFAIMAG')):
            return 'cfa'

        # If not CFA, check the number of axes.
        naxis = header.get('NAXIS', 0)

        # Case for 3-axis images (RGB)
        if naxis == 3 and header.get('NAXIS3') == 3:
            return 'rgb'
        
        # Case for 2-axis images (must be MONO as CFA was already checked)
        elif naxis == 2:
            return 'mono'

        # If none of the above, the format is unsupported.
        raise ValueError(f"Unsupported FITS format (NAXIS={naxis})")

# --- Custom Toolbar Class (without 3D view buttons) ---
class CustomToolbar(NavigationToolbar):
    def __init__(self, canvas, parent):
        super().__init__(canvas, parent)

        # Variable to store the home view limits
        self._home_view = None
        self.set_home_view() # Store the initial view

        # --- Add HELP button to the RIGHT ---
        # Create a spacer widget that will expand to fill the available space
        spacer = QWidget()
        spacer.setSizePolicy(QSizePolicy.Policy.Expanding, QSizePolicy.Policy.Expanding)
        self.addWidget(spacer)

        # add the HELP button, which will be pushed to the far right
        button_help = QPushButton("   HELP   ")
        # Set a unique name for styling with QSS
        button_help.setObjectName("helpButton")
        # Get the standard 'Help' icon and set it on the button
        button_help.setIcon(self.style().standardIcon(QStyle.StandardPixmap.SP_DialogHelpButton))
        # Connect the button click to the parent's show_help method
        button_help.clicked.connect(parent.show_help) 
        self.addWidget(button_help)
    
    def set_home_view(self):
        """ Stores the current axes limits as the home view. """
        ax = self.canvas.figure.gca()
        self._home_view = {
            'xlim': ax.get_xlim(),
            'ylim': ax.get_ylim()
        }

    def home(self, *args, **kwargs):
        """ Overrides the home method to apply the stored custom view. """
        if self._home_view:
            ax = self.canvas.figure.gca()
            ax.set_xlim(self._home_view['xlim'])
            ax.set_ylim(self._home_view['ylim'])
        
        self.canvas.draw_idle()

class CorruptedFilesDialog(QDialog):
    """ A dialog box displays corrupted files and offers the option to discard them."""
    def __init__(self, corrupted_files, parent=None):
        super().__init__(parent)
        self.setWindowTitle("Corrupt File Warning")
        self.setMinimumWidth(400)

        layout = QVBoxLayout(self)

        messaggio = QLabel("The following files were not read correctly (possibly corrupt or in an invalid format) and were skipped during analysis:")
        messaggio.setWordWrap(True)
        layout.addWidget(messaggio)

        # Text area for file list
        self.elenco_file = QTextEdit()
        self.elenco_file.setReadOnly(True)
        self.elenco_file.setText("\n".join(corrupted_files))
        layout.addWidget(self.elenco_file)

        layout_buttons = QHBoxLayout()
        layout.addLayout(layout_buttons)
        
        layout_buttons.addStretch() # Push the buttons to the right

        self.discard_button = QPushButton("Discard All from Sequence")
        self.discard_button.clicked.connect(self.accept)    # Closes the dialog with the code "Accepted"
        layout_buttons.addWidget(self.discard_button)
        
        self.close_button = QPushButton("Close")
        self.close_button.clicked.connect(self.reject)      # Closes the dialog with the code "Rejected"
        layout_buttons.addWidget(self.close_button)

        self.setLayout(layout)

# --- Main Application Class ---
class SequenceAnalyzerApp(QWidget):
    def __init__(self):
        super().__init__()
        self.setWindowTitle(f"Sequence Statistics Analyzer v{VERSION} - (c) Carlo Mollicone AstroBOH")
        
        # --- State Variables ---
        self.help_window = None             # To keep a reference to the help window
        self.annot = None                   # Annotation object for hover tooltip
        self.plotted_lines = {}             # List to store plotted lines for hover detection
        self.analyzed_stats = None          # Store analyzed data to allow replotting without recalculating
        self.analyzed_frames = None
        self.image_type = None              # Store the detected image type
        self.current_sequence = None        # The Current sequence
        self.current_seq_name = None        # String with the name of the sequence
        self.current_frame_index = None     # Index of the current frame
        self.selected_frame_number = None   # Stores the number of the clicked frame
        self.selected_point_coords = None   # Stores the (x, y) coordinates of the clicked point
        self.excluded_points = []           # Stores the coordinates of the excluded points for "X"
        self.processing_thread = None
        self.worker = None

        # --- Siril Connection ---
        # Initialize Siril connection
        self.siril = s.SirilInterface() if s else None
        if self.siril:
            try:
                self.siril.connect()
            except SirilError:
                QMessageBox.critical(self, "Connection Error", "Connection to Siril failed. Make sure Siril is open and ready.")
                QTimer.singleShot(0, self.close)
                return

        try:
            self.siril.cmd("requires", "1.4.0-beta2")
        except s.CommandError:
            QTimer.singleShot(0, self.close)
            return

        # Check if a sequence is loaded
        seq_loaded = self.siril.is_sequence_loaded()

        if seq_loaded:
            # If a sequence is loaded, set the current sequence name and frame index
            self.current_sequence = self.siril.get_seq()
            self.current_seq_name = self.current_sequence.seqname
            self.siril.log(
                f"Sequence '{self.current_seq_name}' is already loaded.",
                s.LogColor.BLUE
            )
        else:
            self.siril.log("No sequence loaded. Use the dropdown to load one.", s.LogColor.RED)

        # --- GUI Setup ---
        self.create_widgets()
        self.center_window()
        
        # Delays drawing the graph by 100ms to give the UI time to settle
        QTimer.singleShot(100, self.plot_placeholder)

    def create_widgets(self):
        """ Create the main UI widgets. """
        main_layout = QHBoxLayout(self)
        
        # Create a QSplitter with horizontal orientation
        splitter = QSplitter(Qt.Orientation.Horizontal)
        main_layout.addWidget(splitter)

        # --- Left Column (Controls) ---
        left_widget = QWidget()
        left_layout = QVBoxLayout(left_widget)
        splitter.addWidget(left_widget)

        # --- Sequence Selection Frame ---
        selection_group = QGroupBox("Sequence Selection")
        selection_layout = QVBoxLayout(selection_group)
        left_layout.addWidget(selection_group)

        self.sequence_dropdown = QComboBox()
        selection_layout.addWidget(self.sequence_dropdown, 1) # Add stretch factor

        self.load_button = QPushButton("Load Sequence")
        self.load_button.clicked.connect(self.load_selected_sequence)
        selection_layout.addWidget(self.load_button)

        # --- Statistics Selection Frame ---
        stats_group = QGroupBox("Plotted Statistics")
        stats_layout = QVBoxLayout(stats_group)
        left_layout.addWidget(stats_group)

        self.cb_min = QCheckBox("Min")
        self.cb_max = QCheckBox("Max")
        self.cb_mean = QCheckBox("Mean")
        self.cb_median = QCheckBox("Median")
        self.cb_std = QCheckBox("Std Dev")
        
        # Set default checks
        self.cb_min.setChecked(True)
        self.cb_max.setChecked(True)
        self.cb_std.setChecked(True)

        stats_layout.addWidget(self.cb_min)
        stats_layout.addWidget(self.cb_max)
        stats_layout.addWidget(self.cb_mean)
        stats_layout.addWidget(self.cb_median)
        stats_layout.addWidget(self.cb_std)

        # Add a separator
        separator = QFrame()
        separator.setFrameShape(QFrame.Shape.HLine)
        separator.setFrameShadow(QFrame.Shadow.Sunken)
        stats_layout.addWidget(separator)

        # Checkbox for deviation plot mode
        self.cb_deviation = QCheckBox("Show Deviation from Median (%)")
        stats_layout.addWidget(self.cb_deviation)
        
        # Connect all checkboxes to the same update function
        for cb in [self.cb_min, self.cb_max, self.cb_mean, self.cb_median, self.cb_std]:
            cb.stateChanged.connect(self.update_plot_visibility)
        
        self.cb_deviation.stateChanged.connect(self.replot_graph)

