    def center_window(self):
        """ Center window using PyQt methods """
        screen_geometry = self.screen().availableGeometry()
        self.resize(1024, 600)
        self.move(
            int((screen_geometry.width() - self.width()) / 2),
            int((screen_geometry.height() - self.height()) / 2)
        )

    def find_sequences(self):
        """ Finds all .seq files in Siril's working directory. """
        cwd = self.siril.get_siril_wd()
        try:
            files = os.listdir(cwd)
            sequences = [f for f in files if f.endswith('.seq')]
            return sorted(sequences, key=str.lower)
        except FileNotFoundError:
            self.siril.log(f"The working directory '{cwd}' was not found.", s.LogColor.RED)
            return []

    def populate_sequence_dropdown(self):
        """ Populates the dropdown menu with the found sequences. """
        self.sequence_dropdown.clear()
        sequences = self.find_sequences()
        if sequences:
            self.sequence_dropdown.addItems(sequences)
            self.load_button.setEnabled(True)
        
            # If a sequence is already loaded at startup, select it in the dropdown
            if self.current_seq_name:
                # The sequence name doesn't have an extension, so we add it
                seq_full_name = f"{self.current_seq_name}.seq"
                index = self.sequence_dropdown.findText(seq_full_name)
                if index != -1:  # If the element is found in the list
                    self.sequence_dropdown.setCurrentIndex(index)
        else:
            self.sequence_dropdown.addItem("No sequences found in CWD")
            self.load_button.setEnabled(False)

    def load_selected_sequence(self):
        """ Loads the sequence selected in the dropdown into Siril. """
        seq_full_name = self.sequence_dropdown.currentText()
        if not seq_full_name or "No sequences found" in seq_full_name:
            self.siril.log("No valid sequence selected to load.", s.LogColor.RED)
            return
        
        # Siril's loadseq command uses the base name without the .seq extension
        seq_base_name = os.path.splitext(seq_full_name)[0]
        
        self.status_label.setText(f"Loading sequence: {seq_base_name}...")
        QApplication.processEvents()
        try:
            self.siril.cmd("load_seq", f"{seq_base_name}")
            self.status_label.setText(f"Sequence '{seq_base_name}' loaded.\nReady to analyze.")
            self.siril.log(f"Sequence '{seq_base_name}' successfully loaded.", s.LogColor.GREEN)
            # Clear plot and stats after loading a new sequence
            self.plot_placeholder()
            self.annot = None
            self.export_button.setEnabled(False) # Disable export button
        except SirilError as e:
            error_msg = f"Failed to load sequence '{seq_base_name}': {e}"
            self.status_label.setText(error_msg)
            self.siril.log(error_msg, s.LogColor.RED)

    def analyze_sequence(self):
        """ Prepares the analysis, performs preliminary checks, and starts the processing thread. """
        if not self.siril.is_sequence_loaded():
            self.status_label.setText("No sequence is currently loaded in Siril.")
            QMessageBox.warning(self, "No Sequence", "Please load a sequence first.")
            return

        self.status_label.setText("Analyzing sequence...")
        QApplication.processEvents() # Update the UI to show the message

        # Reset the list of excluded points only at the start of a NEW analysis
        self.excluded_points = []

        self.current_sequence = self.siril.get_seq()
        # Check if the loaded sequence is of type FITS -> SEQ_REGULAR
        if self.current_sequence.type != s.SequenceType.SEQ_REGULAR:
            error_msg = "Unsupported Sequence Type: This script can only analyze FITS sequences."
            self.status_label.setText(error_msg)
            self.siril.log(error_msg, s.LogColor.RED)
            QMessageBox.warning(self, "Unsupported Sequence", f"{error_msg}\nPlease convert your sequence to FITS first.")
            return
        
        self.current_seq_name = self.current_sequence.seqname

        try:
            # Collect only the files and original frame numbers marked as "included"
            initial_files_to_process = []
            initial_frame_indices = []
            for i in range(self.current_sequence.number):
                if self.current_sequence.imgparam[i].incl:
                    initial_files_to_process.append(self.siril.get_seq_frame_filename(i))
                    initial_frame_indices.append(i + 1) # Store the original frame number (1-based)          
            
            if not initial_files_to_process:
                self.status_label.setText("No frames are included for analysis in the current sequence.")
                QMessageBox.information(self, "No Files", "No frames are selected for analysis in the sequence.")
                return
            
            # --- Prepare and start the thread (for the long operation) ---
            # Update the GUI to show that the analysis is in progress
            self.button_stack.setCurrentIndex(1)    # Show the Stop button
            self.progress_bar.show()                # Show the progress bar
            self.progress_bar.setValue(0)
            self.status_label.setText("Starting analysis...")

            # Create the QThread and the Worker
            self.processing_thread = QThread()
            self.worker = AnalysisWorker(self.siril, initial_files_to_process, initial_frame_indices, self.current_sequence.number)
            self.worker.moveToThread(self.processing_thread)

            # Connect worker signals to GUI slots
            self.processing_thread.started.connect(self.worker.run)
            self.worker.progress.connect(self.progress_bar.setValue)
            self.worker.status.connect(self.status_label.setText)
            self.worker.error_signal.connect(self.processing_error)

            # Ensures the thread is cleaned up properly at the end
            self.processing_thread.finished.connect(self.processing_thread.deleteLater)
            self.processing_thread.finished.connect(self.worker.deleteLater)    # Avoid memory Leaks
            self.processing_thread.finished.connect(self._reset_gui_state)      # Reset GUI state
            self.worker.finished_signal.connect(self.processing_finished)
            self.worker.finished_signal.connect(self.processing_thread.quit)    # Ensure that the thread is closed whether it completes or stops.
            self.worker.cancelled_signal.connect(self.processing_thread.quit)   # Ensure that the thread is closed whether it completes or stops.
            self.worker.error_signal.connect(self.processing_thread.quit)

            # Start the thread
            self.processing_thread.start()
        
        except Exception as e:
            self.export_button.setEnabled(False)
            self.status_label.setText(f"An unexpected error occurred: {e}")
            QMessageBox.critical(self, "Analysis Error", f"An unexpected error occurred during analysis:\n{e}")
            self.siril.log(f"Error during analysis: {e}", s.LogColor.RED)
            traceback.print_exc()

    def stop_processing(self):
        """ Slot for the 'Stop' button. """
        if self.worker:
            self.status_label.setText("Cancelling...")
            self.worker.stop()

    def processing_error(self, message):
        """ Slot for handling errors from the thread. """
        QMessageBox.critical(self, "Processing Error", message)
        self.status_label.setText("Analysis failed.")

    def processing_finished(self, stats, successful_frames, corrupted_files, image_type):
        """ Slot called when the worker is finished. Performs the second part of the analysis and plotting. """
        self.status_label.setText("Finalizing calculations and plotting...")
        QApplication.processEvents()

        # --- Second pass to calculate deviation from the global median ---
        # Calculate statistics only if files have been processed
        if successful_frames:
            # Salva il tipo di immagine ricevuto dal worker
            self.image_type = image_type
            total_successful_files = len(successful_frames)
            
            # helper: compute percent deviation from global median and MAD-based zscore
            def _dev_and_mad_z(values_list):
                arr = np.array(values_list, dtype=float)
                if arr.size == 0:
                    return np.zeros(0), np.zeros(0), 0.0
                global_med = float(np.median(arr))
                # percent deviation from the global median
                if global_med != 0:
                    dev = (arr - global_med) / global_med * 100
                else:
                    dev = np.zeros_like(arr)
                # MAD and robust z (0.6745 factor maps MAD to std for Gaussian)
                mad = np.median(np.abs(arr - np.median(arr)))
                if mad == 0:
                    z = np.zeros_like(arr)
                else:
                    z = 0.6745 * (arr - np.median(arr)) / mad
                return dev.tolist(), z.tolist(), global_med

            if image_type == 'mono':
                # median deviations + MAD zscore
                median_dev, median_mad_z, global_median = _dev_and_mad_z(stats['median'])
                stats['median_dev'] = median_dev
                stats['median_mad_z'] = median_mad_z
                # optional: mean deviation relative to same global median (useful)
                means = np.array(stats['mean'], dtype=float)
                if global_median != 0:
                    stats['mean_dev'] = ((means - global_median) / global_median * 100.0).tolist()
                else:
                    stats['mean_dev'] = [0.0] * total_successful_files
                stats['median_outlier'] = [abs(z) > 3.0 for z in median_mad_z]

            elif image_type in ['rgb', 'cfa']:
                for chan_char in ['r', 'g', 'b']:
                    med_key = f'median_{chan_char}'
                    mean_key = f'mean_{chan_char}'
                    if med_key in stats and mean_key in stats:
                        med_dev, med_mad_z, global_med_chan = _dev_and_mad_z(stats[med_key])
                        stats[f'{med_key}_dev'] = med_dev
                        stats[f'{med_key}_mad_z'] = med_mad_z
                        # mean deviation using the same channel global median
                        means = np.array(stats[mean_key], dtype=float)
                        if global_med_chan != 0:
                            stats[f'{mean_key}_dev'] = ((means - global_med_chan) / global_med_chan * 100.0).tolist()
                        else:
                            stats[f'{mean_key}_dev'] = [0.0] * total_successful_files
                        # mark outliers: |z| > 3
                        stats[f'{med_key}_outlier'] = [abs(z) > 3.0 for z in med_mad_z]
                    else:
                        # safety fallback if channel missing
                        stats[f'{med_key}_dev'] = [0.0] * total_successful_files
                        stats[f'{med_key}_mad_z'] = [0.0] * total_successful_files
                        stats[f'{med_key}_outlier'] = [False] * total_successful_files

            # Store the complete stats and the original frame numbers for replotting
            self.analyzed_stats = stats
            self.analyzed_frames = successful_frames # Use only successful frames
            
            self.replot_graph() # Initial plot
            
            # Handling final messages
            final_msg = f"Analysis of {total_successful_files} / {self.current_sequence.number} {image_type.upper()} frames complete."
            
            if corrupted_files:
                final_msg += f"\nWARNING: {len(corrupted_files)} file(s) could not be read."
                # QMessageBox.warning(self, "Processing Warning",
                #                     f"The analysis is complete, but the following files could not be processed:\n\n" +
                #                     "\n".join(corrupted_files) +
                #                     "\n\nCheck the Siril console for more details.")
                
                corrupted_dialog = CorruptedFilesDialog(corrupted_files, self)
                # Run the dialog modally. If the user presses "Discard All"...
                if corrupted_dialog.exec() == QDialog.DialogCode.Accepted:
                    self.exclude_files_from_sequence(corrupted_files)
                    # NOTE: Optional: Re-executing the analysis will happen inside exclude_files_from_sequence
                    return # Exit to avoid showing duplicate messages
                                
            self.status_label.setText(final_msg)
            self.export_button.setEnabled(True) # Enable export button
        else:
            # If no file has been processed successfully
            final_msg = "Analysis failed. No frames could be processed."
            if corrupted_files:
                final_msg += f"\n{len(corrupted_files)} file(s) were found to be corrupt or unreadable."
                QMessageBox.critical(self, "Analysis Failed",
                                        f"Could not process any frames. The following files caused errors:\n\n" +
                                        "\n".join(corrupted_files))
            self.status_label.setText(final_msg)
            self.plot_placeholder() # Show empty chart
            self.export_button.setEnabled(False) # Disable export button

    def _reset_gui_state(self):
        """ Resets the buttons and progress bar to their initial state. """
        self.button_stack.setCurrentIndex(0)    # Show the Analyze button again
        self.progress_bar.hide()                # Hide the progress bar
        self.progress_bar.setValue(0)

    def replot_graph(self):
        """ Re-plots the graph, typically to switch between absolute and deviation views. """
        if self.analyzed_stats and self.analyzed_frames:
            self.plot_statistics(self.analyzed_frames, self.analyzed_stats)
        # If no data, do nothing (placeholder is already showing)

    def plot_statistics(self, frames, stats):
        """ Plots the calculated statistics on the Matplotlib canvas. """
        # supported values for lines are '-', '--', '-.', ':', 'None', ' ', '', 'solid', 'dashed', 'dashdot', 'dotted'
        self.fig.clear()
        ax = self.fig.add_subplot(111)

        # Store the line objects for the hover event
        self.plotted_lines = {}

        # Determine if we are in deviation mode and set the data key suffix accordingly
        # self.deviation_mode is used by update_annot to format tooltips 
        self.deviation_mode = self.cb_deviation.isChecked()

        # --- Global Deviation Mode ---
        if self.deviation_mode:
            # Plot median_dev only (and mean_dev if available)
            ax.set_title(f"Statistics Deviation from Median for '{self.current_seq_name}'")
            ax.set_ylabel("Deviation from Median (%)")

            if 'median_dev' in stats:   # mono
                self.plotted_lines['median'] = ax.plot(frames, stats['median_dev'], label='Median Dev (%)', marker='.', linestyle='-', picker=5)
            if 'mean_dev' in stats:     # mono
                self.plotted_lines['mean'] = ax.plot(frames, stats['mean_dev'], label='Mean Dev (%)', marker='^', linestyle='dotted', picker=5)

            elif 'median_r_dev' in stats:  # RGB/CFA
                channels = [
                    ('r', 'crimson', 'Median Dev (R)'),
                    ('g', 'forestgreen', 'Median Dev (G)'),
                    ('b', 'royalblue', 'Median Dev (B)')
                ]

                for idx, (chan_char, color, label_base) in enumerate(channels):
                    key = f'median_{chan_char}_dev'
                    if key in stats:
                        self.plotted_lines[f'median_{chan_char}'] = ax.plot(frames, stats[key], label=label_base, marker='.', linestyle='-', color=color, picker=5)
                    mean_key = f'mean_{chan_char}_dev'
                    if mean_key in stats:
                        self.plotted_lines[f'mean_{chan_char}'] = ax.plot( frames, stats[mean_key], label=f'Mean Dev ({chan_char.upper()})', marker='^', linestyle='dotted', color=color, alpha=0.6, picker=5)
                    
                    # Add an empty entry to visually separate channels in the legend, not after the last one
                    if idx < len(channels) - 1:
                        ax.plot([], [], color='none', label=' ')

            ax.set_ylabel("Deviation from Global Median (%)")

        # --- Absolute Statistics Mode ---
        else:
            ax.set_title(f"Per-Frame Statistics for '{self.current_seq_name}'")
            ax.set_ylabel("Pixel Value")

            # Check if we have mono or RGB stats
            if 'mean' in stats: # Mono data
                self.plotted_lines['min'] = ax.plot(frames, stats['min'], label='Min', marker='.', linestyle='-', visible=self.cb_min.isChecked(), picker=5)
                self.plotted_lines['max'] = ax.plot(frames, stats['max'], label='Max', marker='.', linestyle='-', visible=self.cb_max.isChecked(), picker=5)
                self.plotted_lines['mean'] = ax.plot(frames, stats['mean'], label='Mean', marker='.', linestyle='-', visible=self.cb_mean.isChecked(), picker=5)
                self.plotted_lines['median'] = ax.plot(frames, stats['median'], label='Median', marker='.', linestyle='--', visible=self.cb_median.isChecked(), picker=5)
                self.plotted_lines['std'] = ax.plot(frames, stats['std'], label='Std Dev', marker='.', linestyle=':', visible=self.cb_std.isChecked(), picker=5)

            elif 'mean_r' in stats: # RGB data
                # Initialize the lists in the dictionary
                for stat_name in ['min', 'max', 'mean', 'median', 'std']:
                    self.plotted_lines[stat_name] = []

                # Plot Red channel stats
                self.plotted_lines['min'].extend(ax.plot(frames, stats['min_r'], label='Min (R)', marker='.', linestyle='-', color='lightcoral', visible=self.cb_min.isChecked(), picker=5))
                self.plotted_lines['max'].extend(ax.plot(frames, stats['max_r'], label='Max (R)', marker='.', linestyle='-', color='red', visible=self.cb_max.isChecked(), picker=5))
                self.plotted_lines['mean'].extend(ax.plot(frames, stats['mean_r'], label='Mean (R)', marker='.', linestyle='-', color='crimson', visible=self.cb_mean.isChecked(), picker=5))
                self.plotted_lines['median'].extend(ax.plot(frames, stats['median_r'], label='Median (R)', marker='.', linestyle='--', color='firebrick', visible=self.cb_median.isChecked(), picker=5))
                self.plotted_lines['std'].extend(ax.plot(frames, stats['std_r'], label='Std Dev (R)', marker='.', linestyle=':', color='darkred', visible=self.cb_std.isChecked(), picker=5))

                ax.plot([], [], color='none', label=' ')

                # Plot Green channel stats
                self.plotted_lines['min'].extend(ax.plot(frames, stats['min_g'], label='Min (G)', marker='^', linestyle='-', color='lightgreen', visible=self.cb_min.isChecked(), picker=5))
                self.plotted_lines['max'].extend(ax.plot(frames, stats['max_g'], label='Max (G)', marker='^', linestyle='-', color='limegreen', visible=self.cb_max.isChecked(), picker=5))
                self.plotted_lines['mean'].extend(ax.plot(frames, stats['mean_g'], label='Mean (G)', marker='^', linestyle='-', color='forestgreen', visible=self.cb_mean.isChecked(), picker=5))
                self.plotted_lines['median'].extend(ax.plot(frames, stats['median_g'], label='Median (G)', marker='^', linestyle='--', color='seagreen', visible=self.cb_median.isChecked(), picker=5))
                self.plotted_lines['std'].extend(ax.plot(frames, stats['std_g'], label='Std Dev (G)', marker='^', linestyle=':', color='darkgreen', visible=self.cb_std.isChecked(), picker=5))

                ax.plot([], [], color='none', label=' ')

                # Plot Blue channel stats
                self.plotted_lines['min'].extend(ax.plot(frames, stats['min_b'], label='Min (B)', marker='s', linestyle='-', color='lightblue', visible=self.cb_min.isChecked(), picker=5))
                self.plotted_lines['max'].extend(ax.plot(frames, stats['max_b'], label='Max (B)', marker='s', linestyle='-', color='deepskyblue', visible=self.cb_max.isChecked(), picker=5))
                self.plotted_lines['mean'].extend(ax.plot(frames, stats['mean_b'], label='Mean (B)', marker='s', linestyle='-', color='royalblue', visible=self.cb_mean.isChecked(), picker=5))
                self.plotted_lines['median'].extend(ax.plot(frames, stats['median_b'], label='Median (B)', marker='s', linestyle='--', color='blue', visible=self.cb_median.isChecked(), picker=5))
                self.plotted_lines['std'].extend(ax.plot(frames, stats['std_b'], label='Std Dev (B)', marker='s', linestyle=':', color='midnightblue', visible=self.cb_std.isChecked(), picker=5))

        # Add a scatter plot for the "X" markers, initially empty, zorder high to stay on top
        ax.plot([], [], color='none', label=' ')
        self.outlier_marker_plot = ax.scatter([], [], s=100, marker='x', color='black', label='Excluded', zorder=10)

        # Calls for updating the 'X' markers every time the graph is redrawn
        self.update_markers()

        # Highlight outliers (only if we are in deviation mode)
        if self.deviation_mode:
            # --- Case sequence MONO ---
            if 'median_dev' in stats:  # deviation calculated on the single channel
                # Convert deviation values ​​to numpy arrays
                mdev = np.array(stats['median_dev'], dtype=float)

                # If there is an already calculated outlier flag, use it
                if 'median_outlier' in stats:
                    out_idx = [i for i, f in enumerate(stats['median_outlier']) if f]
                else:
                    # Fallback: Consider frames that deviate more than 1% outliers.
                    out_idx = np.where(np.abs(mdev) > 1.0)[0]

                # If there are outliers, plot them with empty markers (red circle)
                if len(out_idx):
                    # Add an empty entry to visually separate channels in the legend
                    ax.plot([], [], color='none', label=' ')
                    ax.plot(np.array(frames)[out_idx], mdev[out_idx], 'o', markersize=8, markeredgecolor='red', markerfacecolor='none', label='Outlier', picker=5)

            # --- RGB or CFA sequence case ---
            elif 'median_r_dev' in stats:  # deviation calculated separately for each channel
                for chan_char, color in [
                    ('r', 'crimson'),
                    ('g', 'forestgreen'),
                    ('b', 'royalblue')
                ]:
                    key = f'median_{chan_char}_dev'
                    if key in stats:
                        # Current channel deviation (R, G or B)
                        mdev = np.array(stats[key], dtype=float)

                        # Use outlier flag if it exists, otherwise fallback to 1% threshold
                        outlier_key = f'median_{chan_char}_outlier'
                        if outlier_key in stats:
                            out_idx = [i for i, f in enumerate(stats[outlier_key]) if f]
                        else:
                            out_idx = np.where(np.abs(mdev) > 1.0)[0]

                        # Outlier plot with empty circle markers, colored by channel
                        if len(out_idx):
                            # Add an empty entry to visually separate channels in the legend
                            ax.plot([], [], color='none', label=' ')
                            ax.plot(np.array(frames)[out_idx], mdev[out_idx], 'o', markersize=8, markeredgecolor=color, markerfacecolor='none', label=f'Outlier ({chan_char.upper()})', picker=5)

        # Legend outside the graph on the right 
        ax.legend(bbox_to_anchor=(1.02, 1), loc='upper left', borderaxespad=0.)
        ax.grid(True)

        # Use the tight_layout method with a rectangle to reserve space on the right.
        # This will not be reset on mouseover.
        # [left, bottom, right, top]
        # self.fig.tight_layout(rect=[0, 0, 1, 1])

        # Create the annotation object, initially invisible
        self.annot = ax.annotate("",
                                 xy=(0,0),
                                 xytext=(20,20),
                                 textcoords="offset points",
                                 bbox=dict(boxstyle="round",
                                           fc="yellow",
                                           alpha=0.5),
                                 arrowprops=dict(arrowstyle="->"),
                                 zorder=10)
        self.annot.set_visible(False)
        
        self.canvas.draw()
