    def update_annot(self, line, ind):
        """ Updates the annotation's position and text. """
        x, y = line.get_data()[0][ind["ind"][0]], line.get_data()[1][ind["ind"][0]]
        self.annot.xy = (x, y)

        label = line.get_label()
        stat_name = label
        channel = 'N/A' # Default for mono case

        # Parse the label to separate stat name and channel
        if '(' in label and ')' in label:
            # e.g.,
            # Absolute Statistics Mode  = "Mean (R)" -> ["Mean", "(R)"]
            # Global Deviation Mode     = "Mean Dev (R)" -> ["Mean", "Dev", "(R)"]
            parts = label.split(' ')
            # The channel is always the last part, stripped of parentheses
            channel = parts[-1].strip('()')
            # The stat name is everything else joined back together
            stat_name = " ".join(parts[:-1])

        # Build detailed tooltip text
        text = f"Frame: {int(x)}\n"
        if self.image_type != 'mono':
            text += f"Channel: {channel}\n"
        text += f"Stat: {stat_name}\n"
        if getattr(self, 'deviation_mode', False):
            text += f"Value: {y:.2f}%"
        else:
            text += f"Value: {y:.2f}"
        
        self.annot.set_text(text)

        # Logic to reverse tooltip placement near edges
        # Get the dimensions of the axis where the point is located
        ax = self.annot.axes
        xlim = ax.get_xlim()
        ylim = ax.get_ylim()

        # Calculate the relative position of the point on the chart (0 to 1)
        # Required to determine if the tooltip is near the top edge
        display_x, display_y = ax.transData.transform((x, y))
        display_width, display_height = self.canvas.get_width_height()

        # Determine the initial offset
        offset_x, offset_y = 20, 20 # Default offset

        # If the point is in the top half of the chart and close enough to the top edge
        # Reverse the direction of the vertical offset to draw below the point
        # Allow for a "margin" from the top edge (e.g., 20% of the axis height)
        if y > (ylim[0] + ylim[1]) / 2 and display_y > display_height * 0.8: # Adjust this threshold if necessary
            offset_y = -50 # Move the tooltip down
            self.annot.arrow_patch.set_visible(True) # Make the arrow invisible if it doesn't make sense
        else:
            self.annot.arrow_patch.set_visible(True) # Make the arrow visible
            offset_y = 20 # Default offset
             
        self.annot.set_position((offset_x, offset_y))
        self.annot.get_bbox_patch().set_alpha(0.8)
        
    def hover(self, event):
        """ Event handler for mouse motion. """
        # Check to avoid errors if the annotation has not yet been created
        if not self.annot:
            return

        vis = self.annot.get_visible()
        if event.inaxes:
            found_line = False
            
            # Iterate through all lines in the plotted_lines dictionary values
            all_lines = [line for stat_lines in self.plotted_lines.values() for line in stat_lines]
            
            for line in all_lines:
                # Only check for hover if the line is currently visible
                if not line.get_visible():
                    continue

                cont, ind = line.contains(event)
                if cont:
                    self.update_annot(line, ind)
                    self.annot.set_visible(True)
                    self.canvas.draw_idle()
                    found_line = True
                    break  # Stop after finding the first line

            if not found_line and vis:
                self.annot.set_visible(False)
                self.canvas.draw_idle()
        elif vis:
            self.annot.set_visible(False)
            self.canvas.draw_idle()

