    def exclude_files_from_sequence(self, filenames_to_exclude):
        """ Excludes the specified files from the Siril sequence and reruns the analysis."""
        if not self.siril or not self.current_sequence:
            return

        # self.status_label.setText(f"Excluding {len(filenames_to_exclude)} files...")
        QApplication.processEvents()

        excluded_count = 0
        filenames_set = set(filenames_to_exclude) # Use a set for faster searches

        # Scan the entire sequence to find indexes of files to exclude
        for i in range(self.current_sequence.number):
            frame_basename = os.path.basename(self.siril.get_seq_frame_filename(i))
            if frame_basename in filenames_set:
                try:
                    self.siril.set_seq_frame_incl(i, False)
                    excluded_count += 1
                except Exception as e:
                    self.siril.log(f"Unable to exclude frame {frame_basename}: {e}", s.LogColor.RED)

        self.siril.log(f"Excluded {excluded_count} corrupt files from the sequence.", s.LogColor.GREEN)
        
        # After excluding files, rerun the analysis to update the graph
        # self.status_label.setText("Re-running the analysis on the updated sequence...")
        QApplication.processEvents()
        # QTimer.singleShot(100, self.analyze_sequence) # With a small delay for stability

    def exclude_frame(self):
        """ Excludes the selected frame from the sequence and adds an 'X' to the graph. """
        if self.selected_frame_number is None:
            return

        try:
            # set_seq_frame_incl uses a 0-based index, so we subtract 1
            frame_index_0based = self.selected_frame_number - 1
            self.siril.set_seq_frame_incl(frame_index_0based, False)
            
            # Use the coordinates saved by on_pick directly. No calculations needed!
            point = self.selected_point_coords
            if point not in self.excluded_points:
                self.excluded_points.append(point)
            self.update_markers()

            status_msg = f"Frame #{self.selected_frame_number} EXCLUDED."
            self.status_label.setText(status_msg)
            self.siril.log(status_msg, s.LogColor.GREEN)

        except Exception as e:
            error_msg = f"Error excluding frame: {e}"
            self.status_label.setText(error_msg)
            self.siril.log(error_msg, s.LogColor.RED)
        
        # Disable buttons after action
        self.include_button.setEnabled(False)
        self.exclude_button.setEnabled(False)
        self.selected_frame_number = None

    def include_frame(self):
        """ Includes the selected frame in the sequence and removes the 'X' from the graph. """
        if self.selected_frame_number is None:
            return
            
        try:
            frame_index_0based = self.selected_frame_number - 1
            self.siril.set_seq_frame_incl(frame_index_0based, True)

            # Remove the dot from the exclude list, if present
            self.excluded_points = [p for p in self.excluded_points if p[0] != self.selected_frame_number]
            self.update_markers()

            status_msg = f"Frame #{self.selected_frame_number} INCLUDED."
            self.status_label.setText(status_msg)
            self.siril.log(status_msg, s.LogColor.GREEN)

        except Exception as e:
            error_msg = f"Error including frame: {e}"
            self.status_label.setText(error_msg)
            self.siril.log(error_msg, s.LogColor.RED)
            
        # Disable buttons after action
        self.include_button.setEnabled(False)
        self.exclude_button.setEnabled(False)
        self.selected_frame_number = None

    def export_to_csv(self):
        """ Exports the currently displayed graph data to a CSV file. """
        if not self.analyzed_stats:
            QMessageBox.warning(self, "No Data", "Please analyze a sequence before exporting.")
            return

        default_filename = f"stats_{self.current_seq_name}.csv"
        initial_dir = self.siril.get_siril_wd()
        filename, _ = QFileDialog.getSaveFileName(self, "Export to CSV", os.path.join(initial_dir, default_filename), "CSV Files (*.csv)")

        if not filename:
            return # User cancelled

        try:
            headers = ["Frame"]
            data_keys = []

            # Check the mode (absolute or deviation) and prepare headers and keys accordingly.
            if self.deviation_mode:
                # --- DEVIATION TABLE ---
                if self.image_type == 'mono':
                    headers.extend(["Median Deviation (%)", "Mean Deviation (%)"])
                    data_keys.extend(['median_dev', 'mean_dev'])
                elif self.image_type in ['rgb', 'cfa']:
                    for chan in ['R', 'G', 'B']:
                        headers.extend([f"Median Dev ({chan}) (%)", f"Mean Dev ({chan}) (%)"])
                    for chan_char in ['r', 'g', 'b']:
                        data_keys.extend([f'median_{chan_char}_dev', f'mean_{chan_char}_dev'])
            else:
                # --- ABSOLUTE STATISTICS TABLE ---
                if self.image_type == 'mono':
                    headers.extend(["Min", "Max", "Mean", "Median", "Std Dev"])
                    data_keys.extend(['min', 'max', 'mean', 'median', 'std'])
                elif self.image_type in ['rgb', 'cfa']:
                    for chan in ['R', 'G', 'B']:
                        headers.extend([f"Min ({chan})", f"Max ({chan})", f"Mean ({chan})", f"Median ({chan})", f"Std Dev ({chan})"])
                    for chan_char in ['r', 'g', 'b']:
                        for stat in ['min', 'max', 'mean', 'median', 'std']:
                            data_keys.append(f'{stat}_{chan_char}')

            # Write the data to the CSV file
            with open(filename, 'w', newline='') as csvfile:
                writer = csv.writer(csvfile)
                writer.writerow(headers)
                
                num_frames = len(self.analyzed_frames)
                for i in range(num_frames):
                    row = [i + 1] # Frame number
                    for key in data_keys:
                        # Fallback to an empty string if a key (e.g., a _dev key) is missing
                        value = self.analyzed_stats.get(key, [None]*num_frames)[i]
                        row.append(f"{value:.6f}" if value is not None else "")
                    writer.writerow(row)
            
            self.status_label.setText(f"Data successfully exported to:\n{os.path.basename(filename)}")
            self.siril.log(f"Data exported to {filename}", s.LogColor.GREEN)

        except Exception as e:
            error_msg = f"Error during export: {e}"
            self.status_label.setText(error_msg)
            self.siril.log(error_msg, s.LogColor.RED)
            QMessageBox.critical(self, "Export Error", error_msg)

    def show_help(self):
        """
        Create and display the help window.
        We keep a reference to it to prevent it from being deleted by the garbage collector.
        """
        # Check if a help window is already open
        if self.help_window is None:
            self.help_window = HelpWindow(self)
            # When the window is closed, reset the reference
            self.help_window.finished.connect(self.on_help_window_closed)
        
        self.help_window.show()
        self.help_window.activateWindow() # Bring window to front
    
    def on_help_window_closed(self):
        """ Slot to reset the reference to the help window. """
        self.help_window = None

    def closeEvent(self, event):
        """
        This event handler is called automatically by PyQt when the user closes the window.
        It handles the cleanup and disconnection from Siril.
        """
        try:
            if self.siril:
                self.siril.log("Window closed. Script cancelled by user.", s.LogColor.BLUE)
                self.siril.disconnect()
        except Exception as e:
            print(f"An error occurred during cleanup: {e}")
        
        event.accept()

